package kaoyan.fushi;

public class Ceui {
/**
 * 
 * @param args
 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i=0;
		int j=0;
		for(;i<4;i++);
		System.out.println(i);
		
		for(;i<4;);
		System.out.println(i);
		
		while(++j<4);
		System.out.println(j);
		
	}

}
